<?php



/**
 * This is the base list class for the  Offer class.
 *
 * Do not edit this file, this file is overwritten on any code regenerations. Make any changes you need to the subclass.
 *
 * @package My QCubed Application
 * @subpackage PanelBaseObjects
 */
abstract class OfferListPanelGen extends QPanel {
	/** @var QPanel **/
	protected $pnlFilter;

	/** @var QTextBox **/
	protected $txtFilter;

	/** @var QPanel **/
	protected $pnlButtons;

	/** @var QButton **/
	protected $btnNew;

	/** @var OfferList **/
	protected $dtgOffers;


	public function __construct($objParent, $strControlId = null) {
		parent::__construct($objParent, $strControlId);

		$this->CreateFilterPanel();
		$this->dtgOffers_Create();
		$this->dtgOffers->SetDataBinder('BindData', $this);
		$this->CreateButtonPanel();
	}

   /**
	* Creates the data grid and prepares it to be row clickable. Override for additional creation operations.
	**/
	protected function dtgOffers_Create() {
		$this->dtgOffers = new OfferList($this);
		$this->dtgOffers_CreateColumns();
		$this->dtgOffers_MakeEditable();
		$this->dtgOffers->RowParamsCallback = [$this, "dtgOffers_GetRowParams"];
			$this->dtgOffers->LinkedNode = QQN::Offer();
	}

   /**
	* Calls the list connector to add the columns. Override to customize column creation.
	**/
	protected function dtgOffers_CreateColumns() {
		$this->dtgOffers->CreateColumns();
	}

	protected function dtgOffers_MakeEditable() {
		$this->dtgOffers->AddAction(new QCellClickEvent(0, null, null, true), new QAjaxControlAction($this, 'dtgOffers_CellClick', null, null, QCellClickEvent::RowValue));
		$this->dtgOffers->AddCssClass('clickable-rows');
	}

	protected function dtgOffers_CellClick($strFormId, $strControlId, $strParameter) {
		if ($strParameter) {
			$this->EditItem($strParameter);
		}
	}
	public function dtgOffers_GetRowParams($objRowObject, $intRowIndex) {
		$strKey = $objRowObject->PrimaryKey();
		$params['data-value'] = $strKey;
		return $params;
	}

	/**
	 *
	 **/
	protected function CreateFilterPanel() {
		$this->pnlFilter = new QPanel($this);	// div wrapper for filter objects
		$this->pnlFilter->AutoRenderChildren = true;

		$this->txtFilter = new QTextBox($this->pnlFilter);
		$this->txtFilter->Placeholder = QApplication::Translate('Search...');
		$this->txtFilter->TextMode = QTextMode::Search;
		$this->AddFilterActions();
	}

	protected function AddFilterActions() {
		$this->txtFilter->AddAction(new QInputEvent(300), new QAjaxControlAction ($this, 'FilterChanged'));
		$this->txtFilter->AddActionArray(new QEnterKeyEvent(),
			[
				new QAjaxControlAction($this, 'FilterChanged'),
				new QTerminateAction()
			]
		);
	}

	protected function FilterChanged() {
		$this->dtgOffers->Refresh();
	}

	/**
	 *	Bind Data to the list control.
	 **/
	public function BindData() {
		$objCondition = $this->GetCondition();
		$this->dtgOffers->BindData($objCondition);
	}


	/**
	 *  Get the condition for the data binder.
	 *  @return QQCondition;
	 **/
	protected function GetCondition() {
		$strSearchValue = $this->txtFilter->Text;
		$strSearchValue = trim($strSearchValue);

		if (is_null($strSearchValue) || $strSearchValue === '') {
			 return QQ::All();
		} else {
			return QQ::OrCondition(
				QQ::Equal(QQN::Offer()->IdOffer, $strSearchValue),
            QQ::Like(QQN::Offer()->Description, "%" . $strSearchValue . "%"),
            QQ::Like(QQN::Offer()->OfferedCoins, "%" . $strSearchValue . "%"),
            QQ::Equal(QQN::Offer()->IdRestaurant, $strSearchValue),
            QQ::Equal(QQN::Offer()->MaxOffers, $strSearchValue),
            QQ::Equal(QQN::Offer()->AppliedOffers, $strSearchValue),
            QQ::Equal(QQN::Offer()->MaxCoins, $strSearchValue),
            QQ::Equal(QQN::Offer()->Status, $strSearchValue)
			);
		}

	}



	/**
	 *
	 **/
	protected function CreateButtonPanel() {
		$this->pnlButtons = new QPanel ($this);
		$this->pnlButtons->AutoRenderChildren = true;

		$this->btnNew = new QJqButton ($this->pnlButtons);
		$this->btnNew->Text = QApplication::Translate ('New');
		$this->btnNew->AddAction (new QClickEvent(), new QAjaxControlAction ($this, 'btnNew_Click'));
	}

	protected function btnNew_Click($strFormId, $strControlId, $strParameter) {
		$this->EditItem();
	}

	protected function EditItem ($strKey = null) {
		$strQuery = '';
		if ($strKey) {
			$strQuery =  '?intIdOffer=' . $strKey;
		}
		$strEditPageUrl = __VIRTUAL_DIRECTORY__ . __FORMS__ . '/offer_edit.php' . $strQuery;
		QApplication::Redirect ($strEditPageUrl);
	}

}
